    @GetMapping("/loadImg")
    public ResponseEntity<InputStreamResource> loadImg(@RequestParam("filename") String filename){
        Criteria criteria = GridFsCriteria.where("filename").is(filename);
        Query query = new Query(criteria);
        GridFSDBFile imgFile = gridFsTemplate.findOne(query);
        if (imgFile==null) return null;
        HttpHeaders headers = new HttpHeaders();
        headers.add("Cache-Control", "no-cache, no-store, must-revalidate");
        headers.add("Content-Disposition", "attachment; filename=" + System.currentTimeMillis() + ".jpg");
        headers.add("Pragma", "no-cache");
        headers.add("Expires", "0");
        return ResponseEntity
                .ok()
                .headers(headers)
                .contentLength(imgFile.getLength())
                .contentType(MediaType.parseMediaType("application/octet-stream"))
                .body(new InputStreamResource(imgFile.getInputStream()));
    }
